export class MovieCardComponent {
    constructor(UserRegistration, router, dialog) {
        this.UserRegistration = UserRegistration;
        this.router = router;
        this.dialog = dialog;
        this.movies = [];
        this.allMovies = [];
        this.user = {};
        this.userFavoriteMovies = {};
        this.favoriteView = false;
    }
    /**
     * component is used
     */
    ngOnInit() {
        this.favoriteView ? this.populateFavoriteMoviesInMoviesArray() : this.initializeApp();
    }
    initializeApp() {
        console.log('All movies view');
        this.getMovies();
        this.getUser();
    }
    determineInitialFavorites() {
        console.log('inside determineFavorites');
        console.log(this.user);
        console.log(this.movies);
        this.movies.forEach(movie => {
