                let collectFavorites = [];
                this.user.FavoriteMovies.forEach((id) => {
                    collectFavorites.push(resp.find(movie => movie._id === id));
                });
                this.movies = collectFavorites;
                console.log(this.movies);
            }
            else {
                this.movies = resp;
            }
            console.log(this.movies);
            return this.movies;
        });
    }
