        this.dialog.open(GenreComponent, {
            width: '280px',
            data: genre
        });
    }
    openDirectorDialog(director) {
        this.dialog.open(DirectorComponent, {
            width: '280px',
            data: director
        });
    }
    openSynopsisDialog(description) {
        this.dialog.open(SynopsisComponent, {
            width: '280px',
            data: {
                description
            }
        });
    }
    // Adding a Favorite Movie
    getUser() {
        this.UserRegistration.getUser().subscribe((result) => {
            this.user = result;
            console.log(this.user);
        });
    }
    duplicateFavoriteMovie(id) {
        this.getUser();
        if (this.user.FavoriteMovies.includes(id)) {
            return true;
        }
        return false;
    }
    addFavoriteMovie(id) {
        if (!this.duplicateFavoriteMovie(id)) {
            console.log(this.duplicateFavoriteMovie(id));
            this.UserRegistration.addFavoriteMovie(id).subscribe(result => console.log(result));
